    paginatedItems() {
      const start = (this.currentPage - 1) * this.itemsPerPage;
      const end = start + this.itemsPerPage;
      return this.items.slice(start, end);
    },
    paginationPages() {
      const pages = [];
      if (this.totalPages <= 5) {
        // Si le total de pages est inférieur ou égal à 5, afficher tous les boutons
        for (let i = 1; i <= this.totalPages; i++) pages.push(i);
      } else if (this.currentPage <= 3) {
        // Si on est au début, afficher les trois premiers, "..." et le dernier
        pages.push(1, 2, 3, '...', this.totalPages);
      } else if (this.currentPage >= this.totalPages - 2) {
        // Si on est proche de la fin, afficher le premier, "..." et les trois derniers
        pages.push(1, '...', this.totalPages - 2, this.totalPages - 1, this.totalPages);
      } else {
        // Sinon, afficher le premier, "...", la page courante ±1, "..." et le dernier
        pages.push(1, '...', this.currentPage - 1, this.currentPage, this.currentPage + 1, '...', this.totalPages);
      }

      // Limiter à 5 boutons en utilisant "..."
      if (pages.length > 5) {
        if (this.currentPage <= 3) {
          return pages.slice(0, 4).concat(['...', this.totalPages]);
        } else if (this.currentPage >= this.totalPages - 2) {
          return [1, '...'].concat(pages.slice(-4));
        } else {
