  methods: {
    changePage(page) {
      if (page !== '...') {
        this.currentPage = page;
      }
    },
    prevPage() {
      if (this.currentPage > 1) {
        this.currentPage -= 1;
      }
    },
    nextPage() {
      if (this.currentPage < this.totalPages) {
        this.currentPage += 1;
      }
    },
    ellipsisClick() {
      // Avance ou recule dans la pagination en fonction de la position de l'ellipse
      if (this.currentPage < this.totalPages - 3) {
        this.currentPage += 3;
      } else {
        this.currentPage -= 3;
      }
    }
